function stripInvisibleChars(st: string) {
  // Ensure we have a string
  st = st || "";

  // Remove invisible characters like control characters
  st = st.replace(invisibleCharRegex, "");

  return st;
}

export function slugify(str: string, options: any = {}) {
  // Ensure we have a string
  str = str || "";

  // Strip all characters that cannot be printed
  str = stripInvisibleChars(str);

  // Handle the £ symbol separately, since it needs to be removed before the unicode conversion.
  str = str.replace(/£/g, "-");

  // Remove non ascii characters
  str = slugifyExt(str);

  // Replace URL reserved chars: `@:/?#[]!$&()*+,;=` as well as `\%<>|^~£"{}` and \`
  str = str
    .replace(
      /(\s|\.|@|:|\/|\?|#|\[|\]|!|\$|&|\(|\)|\*|\+|,|;|=|\\|%|<|>|\||\^|~|"|\{|\}|`|–|—)/g,
      "-"
    )
    // Remove apostrophes
    .replace(/'/g, "")
    // Make the whole thing lowercase
    .toLowerCase();

  // These changes are optional changes, we can enable/disable these
  if (!options.requiredChangesOnly) {
    // Convert 2 or more dashes into a single dash
    str = str
      .replace(/-+/g, "-")
      // Remove trailing dash
      .replace(/-$/, "")
      // Remove any dashes at the beginning
      .replace(/^-/, "");
  }

  // Handle whitespace at the beginning or end.
  str = str.trim();

  return str;
}

export async function spawnService(
  service: string,
  method: string,
  args: string[]
) {
  const child = childProcess.spawn("tsx", [
    "src/index.ts",
    service,
    method,
    ...args,
  ]);
  child.stdout.on("data", (data) => {
    console.log(`stdout: ${data}`);
  });
  child.stderr.on("data", (data) => {
    console.error(`stderr: ${data}`);
  });
  return new Promise((ok) => {
    child.on("close", (code) => {
      console.log(`child process exited with code ${code}`);
      ok(code);
    });
  });
}

export async function dnsResolveNoCache(domain: string, type: string) {
  // validate! otherwise we'll get f-ed by a code injection
  // https://stackoverflow.com/a/26987741
  const rx =
    /^(((?!-))(xn--|_)?[a-z0-9-]{0,61}[a-z0-9]{1,1}\.)*(xn--)?([a-z0-9][a-z0-9\-]{0,60}|[a-z0-9-]{1,30}\.[a-z]{2,})$/;

  const types = ["TXT", "A", "CNAME"];
  if (!domain.match(rx)) throw new Error("Invalid domain");
  if (!types.includes(type)) throw new Error("Invalid dns type");

  return new Promise<string[]>((ok, err) => {
    const cmd = `dig ${domain} ${type} +trace | grep ${type} | awk '{print $NF}'`;
    console.log(`exec cmd "${cmd}"`);
    childProcess.exec(cmd, (e, stdout, stderr) => {
      if (e) {
        console.log("dns error", domain, type, e, stderr);
        err(e);
        return;
      } else {
        console.log("dns", domain, type, `"${stdout}"`);
        const recs = stdout
          .split("\n")
          .map((s) => s.trim())
          .filter((s) => !s.startsWith(";"))
          .map((s) => {
            if (s.startsWith('"') && s.endsWith('"'))
              return s.substring(1, s.length - 1);
            else return s;
          })
          .filter((s) => !!s);
        console.log("recs", recs);
        ok(recs);
      }
    });
  });
}

// https://stackoverflow.com/a/12646864
export function shuffleArray(array: any[]) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
}

export function now() {
  return Math.floor(Date.now() / 1000);
}

export function calcPaidUntil(due_timestamp: number, period: string) {
  const date = new Date(due_timestamp * 1000);
  switch (period) {
    case "1m": {
      // overflow is REQUIRED to make sure we roll
      // over to next year if current month is Dec
      const ms = date.setMonth(date.getMonth() + 1);
      return Math.floor(ms / 1000);
    }
    default:
      throw new Error("Unknown period");
  }
}
